CREATE  TABLE IF NOT EXISTS `reminiscensdb`.`Contributed_Memento` (
  `contributed_memento_id` INT NOT NULL AUTO_INCREMENT ,
  `headline` VARCHAR(254) NULL ,
  `text` TEXT NULL ,
  `type` VARCHAR(32) NULL DEFAULT NULL COMMENT 'VIDEO, IMAGE, AUDIO, TEXT' ,
  `category` VARCHAR(45) NULL DEFAULT NULL COMMENT 'PICTURES, SONG, PEOPLE, STORY, FILM, TV' ,
  `source` VARCHAR(45) NULL ,
  `source_url` TEXT NULL ,
  `resource_url` TEXT NULL DEFAULT NULL ,
  `resource_thumbnail_url` TEXT NULL DEFAULT NULL ,
  `author` TEXT NULL ,
  `collection` TEXT NULL ,
  `nationality` TEXT NULL ,
  `locale` VARCHAR(45) NULL ,
  `haschcode` TEXT NULL ,
  `file_hashcode` TEXT NULL ,
  `fuzzy_startdate` BIGINT NULL COMMENT 'If NULL, inherits the one of Life_Event' ,
  `location_start_id` BIGINT NULL COMMENT 'If NULL, inherits the one of Life_Event' ,
  `fuzzy_enddate` BIGINT NULL ,
  `location_end_id` BIGINT NULL ,
  PRIMARY KEY (`contributed_memento_id`) ,
  INDEX `rel_multimedia_taken_location` (`location_start_id` ASC) ,
  INDEX `fk_Memento_Fuzzy_Date1_idx` (`fuzzy_startdate` ASC) ,
  INDEX `fk_Memento_Fuzzy_Date2_idx` (`fuzzy_enddate` ASC) ,
  INDEX `fk_Contributed_Memento_Location1_idx` (`location_end_id` ASC) ,
  CONSTRAINT `rel_multimedia_taken_location0`
    FOREIGN KEY (`location_start_id` )
    REFERENCES `reminiscensdb`.`Location` (`location_id` ),
  CONSTRAINT `fk_Memento_Fuzzy_Date10`
    FOREIGN KEY (`fuzzy_startdate` )
    REFERENCES `reminiscensdb`.`Fuzzy_Date` (`fuzzy_date_id` )
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `fk_Memento_Fuzzy_Date20`
    FOREIGN KEY (`fuzzy_enddate` )
    REFERENCES `reminiscensdb`.`Fuzzy_Date` (`fuzzy_date_id` )
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `fk_Contributed_Memento_Location1`
    FOREIGN KEY (`location_end_id` )
    REFERENCES `reminiscensdb`.`Location` (`location_id` )
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB
AUTO_INCREMENT = 32
DEFAULT CHARACTER SET = latin1;




